// ../config.js accepts options via environment variables
const options = {}

if (process[REDACTED_FILENAME].DOTENV_CONFIG_ENCODING != null) {
  options.encoding = process[REDACTED_FILENAME].DOTENV_CONFIG_ENCODING
}

if (process[REDACTED_FILENAME].DOTENV_CONFIG_PATH != null) {
  options.path = process[REDACTED_FILENAME].DOTENV_CONFIG_PATH
}

if (process[REDACTED_FILENAME].DOTENV_CONFIG_QUIET != null) {
  options.quiet = process[REDACTED_FILENAME].DOTENV_CONFIG_QUIET
}

if (process[REDACTED_FILENAME].DOTENV_CONFIG_DEBUG != null) {
  options.debug = process[REDACTED_FILENAME].DOTENV_CONFIG_DEBUG
}

if (process[REDACTED_FILENAME].DOTENV_CONFIG_OVERRIDE != null) {
  options.override = process[REDACTED_FILENAME].DOTENV_CONFIG_OVERRIDE
}

if (process[REDACTED_FILENAME].DOTENV_CONFIG_DOTENV_KEY != null) {
  options.DOTENV_KEY = process[REDACTED_FILENAME].DOTENV_CONFIG_DOTENV_KEY
}

module.exports = options
